(async () => {
    // for each files 
    for (let file of event.files) {
        // resize image
        let image = await this.util.resize(file, { quality: 0.9, width: 800, height: 600 });
        image.name = file.name;

        // upload to firestore
        await this.storage.upload(`images/${file.name}`, image);
    }
})();
