screens.click:
if(this.data.password != this.data.password_confirm) {
	this.snackBar.open("Password does not match", null, {duration: 2000})    
} 

else {
	let data = {
        _id: this.data._id,
		password: this.data.password,
		password_confirm: this.data.password_confirm,
		password_old: this.data.password_old
	};
	//
	this.rest.request("/api/user/profile", data, "post")					
		.subscribe(response => {
			if(response.error) {
				alert(response.error)
			} else {
				this.snackBar.open("Cambiato", null, {duration: 2000})    
				this.event.send({name:"close-dialog"});
			}					
		});			
}
