screens.click:
this.event.send({name:'close-dialog'});

let data = JSON.parse(JSON.stringify(this.data));
delete data._params_;
this.rest.request(
  `/api/user/users?_id=${this.data._id}`, data, 'put'
).subscribe(
  response => {
    this.snackBar.open('Cambiato', null, {duration: 2000})
  }
);
